package es.iesjandula.antonio_arcediano_consuegra.transparencia14;
public class aacVariablesJava01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=144, y=999;
		String nombre="Antonio Arcediano Consuegra";
		System.out.println("La variable de x es "+x);
		System.out.println("La varianle de y es "+y);
		System.out.println("La suma de "+x+" + "+y+" es igual a "+(x+y));
		System.out.println("La resta de "+x+" - "+y+" es igual a "+(x-y));
		System.out.println("La multiplicacion de "+x+" x "+y+" es igual a "+(x*y));
		System.out.println("La division de "+x+" / "+y+" es igual a "+(x/y));
		System.out.println(nombre);

	}

}
